mapper.insert:
INSERT INTO restaurant
   SET admin_id = #{adminId}
     , category_id = #{categoryId}
     , restaurant_name = #{restaurantName}
     , restaurant_address = #{restaurantAddress}
     , restaurant_number = #{restaurantNumber}
     , business_number = #{businessNumber}
     , operating_hours = #{operatingHours}
     , restaurant_description = #{restaurantDescription}
     , max_capacity = #{maxCapacity}
     , lat = #{lat}
     , lng = #{lng}

mapper.select:
SELECT F.restaurant_name AS restaurantName , F.restaurant_address AS restaurantAddress , F.restaurant_id AS restaurantId , F.lat, F.lng , (6371 * ACOS(COS(RADIANS(40)) * COS(RADIANS(F.lat)) * COS(RADIANS(F.lng) - RADIANS(127)) + SIN(RADIANS(40)) * SIN(RADIANS(F.lat)))) AS DISTANCE, IFNULL(( SELECT AVG(ABS(TIMESTAMPDIFF(MINUTE, B.reservation_time, C.use_date))) FROM `order` A INNER JOIN reservation B ON A.order_id = B.order_id INNER JOIN ticket C ON B.order_id = C.order_id WHERE A.restaurant_id = F.restaurant_id GROUP BY A.restaurant_id ), 40) AS avgRestaurant FROM restaurant F WHERE `status` = 0
AND F.restaurant_name LIKE '%${searchFilter}%'
AND lat > #{sysMinLat} AND lat < #{sysMaxLat} AND lng > #{sysMinLng} AND lng < #{sysMaxLng}
ORDER BY F.restaurant_name ASC;
ORDER BY distance ASC;

mapper.insert:
INSERT INTO holiday
   SET restaurant_id = #{restaurantId}
     , closed_days = #{closedDays}

mapper.update:
UPDATE restaurant


              restaurant_name = #{restaurantName}


            , restaurant_address = #{restaurantAddress}


            , restaurant_number = #{restaurantNumber}


            , operating_hours = #{operatingHours}

            , restaurant_description = #{restaurantDescription}

            , `status` = #{status}


            , max_capacity = #{maxCapacity}


            , lat = #{lat}


            , lng = #{lng}


 WHERE restaurant_id = #{restaurantId}

UPDATE holiday A
 INNER JOIN restaurant B
    ON A.restaurant_id = B.restaurant_id


        A.closed_days = #{closedDays}


 WHERE B.restaurant_id = #{restaurantId}
   AND A.holiday_id = #{holidayId}

mapper.select:
SELECT B.order_id AS orderId, B.created_at AS createdAt, C.user_id AS userId, C.name AS userName , B.reservation_yn AS reservationYn, IF(B.reservation_yn = 1, '예약 주문', '앉아서 주문') AS reservationYnStr, B.restaurant_id AS restaurantId , ( SELECT SUM(`point`) FROM user_payment_member WHERE order_id = B.order_id ) AS `point` FROM ticket A INNER JOIN `order` B ON A.order_id = B.order_id INNER JOIN user C ON B.user_id = C.user_id WHERE A.ticket_status = 1 AND B.restaurant_id = #{restaurantId}
AND ( DATE_FORMAT(A.use_date, '%Y-%m-%d') >= #{startDate} AND DATE_FORMAT(A.use_date, '%Y-%m-%d') <= #{endDate} )
AND ( DATE_FORMAT(A.use_date, '%Y-%m-%d') >= #{startDate} )
AND ( DATE_FORMAT(A.use_date, '%Y-%m-%d') <= #{endDate} )
LIMIT #{startIdx}, #{size}
