mapper.insert:
INSERT INTO `restaurant_menu`
SET category_id = #{categoryId}
, menu_name = #{menuName}
, menu_pic = #{menuPic}
, price = #{price}

    , details = #{details}

, available = #{available}

mapper.select:
SELECT menu_name AS menuName, price, details, available, menu_pic AS menuPic, menu_id AS menuId
FROM restaurant_menu
WHERE category_id = #{categoryId};

mapper.update:
UPDATE restaurant_menu


        menu_name = #{menuName}


        , price = #{price}

        , details = #{details}

        , category_id = #{categoryId}


        , available = #{available}


WHERE menu_id = #{menuId}

mapper.insert:
INSERT INTO restaurant_menu_category
   SET restaurant_id = #{restaurantId}
     , category_name = #{categoryName}

mapper.select:
SELECT category_id AS categoryId, category_name AS categoryName
FROM restaurant_menu_category
WHERE restaurant_id = #{restaurantId}

SELECT COUNT(*)
FROM restaurant_menu
WHERE category_id = #{categoryId}
