mapper.update:
UPDATE restaurant_menu
   SET menu_pic = #{picName}
 WHERE menu_id = #{menuId}

mapper.delete:
DELETE FROM restaurant_pic
WHERE pic_id IN

    #{picId}

AND restaurant_id = #{restaurantId}

mapper.select:
SELECT file_path
FROM restaurant_pic
WHERE pic_id = #{picId}

SELECT restaurant_id AS restaurantId, file_path AS filePath
FROM restaurant_pic
WHERE restaurant_id = #{restaurantId}
